mapper.select:
SELECT *
FROM staff
WHERE id = #{staffId}
  AND status = 'USED';

SELECT *
FROM staff
WHERE name = #{name}
  AND status = 'USED';

mapper.update:
UPDATE staff
SET
status = 'USED'
,name=#{staffEntity.name}
,phone_number=#{staffEntity.phoneNumber}
,type=#{staffEntity.type}
,task_id=#{staffEntity.taskId}
,position_id=#{staffEntity.positionId}
WHERE id = #{staffId}

UPDATE staff
SET status = 'DELETED'
WHERE id = #{staffId}
