mapper.select:
SELECT *
FROM staff
WHERE id = #{staffId}
  AND status = 'USED';

mapper.update:
UPDATE staff
name=#{staffEntity.name}, phone_number=#{staffEntity.phoneNumber}, type=#{staffEntity.type}, task_id=#{staffEntity.taskId}, position_id=#{staffEntity.positionId},
WHERE id = #{staffId}

UPDATE staff
SET status = 'DELETED'
WHERE id = #{staffId}

mapper.select:
SELECT s.name as staff_name, phone_number, type, t.name as task_name, csr.name as company_name, code, p.name as position_name FROM staff s
left join positions p on s.position_id = p.id
left join task t on s.task_id = t.id
left join ( select staff_id, name, company_id from company_staff cs inner join company c on cs.company_id = c.id ) csr on csr.staff_id = s.id
status = 'USED'
AND s.position_id = #{positionId}
AND s.task_id = #{taskId}
AND csr.company_id = #{companyId}

select *
from staff
where name = #{keyword}
  or type LIKE CONCAT('%', #{keyword}, '%');
